const {building, sequence} = msg
const sequenceId = msg.sequenceId || msg.sequence || msg.id;

let outputs = [null, null, null];

if (!msg.error) {  // valid data
    let phase;

    if (msg.phase === undefined) {
        phase = sequence.phase || 0;

        phase = (phase < sequence.phases.length - 1 ? phase + 1 : 0);
    } else
        phase = msg.phase;

    if (phase > sequence.phases.length - 1) phase = 0;

    building.sequences[sequenceId].phase = phase;

    const targetPhase = sequence.phases[phase];
    const firstDevice = G.getDevice(msg.accountId, msg.buildingId, targetPhase[0].device);
    const roomId = firstDevice.room;

    outputs[2] = {accountId: msg.accountId, buildingId: msg.buildingId, room: roomId || "all", payload: sequence.sound || "airplaneding"};

    outputs[0] = targetPhase.map(deviceState => {
        const deviceId = deviceState.device || deviceState.deviceId;

        return {
            accountId: msg.accountId,
            buildingId: msg.buildingId,
            duration: deviceState.duration,
            deviceId: deviceId,
            eventId: msg.eventId,
            ruleId: msg.ruleId,
            payload: deviceState.state
        };
    });

