const [G, D, S] = global.get(["globals", "dataEngine", "server"]);

G.handleQuery(msg, node, {nostatus: true});

const pay = (msg.payload && typeof msg.payload === "object") ? msg.payload : {};
const offTime = (pay.offTime ? new Date(pay.offTime) : (pay.duration ? new Date(new Date().getTime() + (pay.duration * 60 * 1000)) : null));
const { account, building, device, device: {api}, deviceId, accountId, buildingId, cause, hue, saturation, brightness } = msg
const { delay = 0, priority = 1, ruleId, eventId, source = msg.source} = pay

if (!msg.error) {
    if (device) {
        const oldState = (D.getDataNode(accountId, buildingId, [deviceId, "on"].join(".")) || {value: false, timestamp: 0});
        const oldOn = oldState.value;
        const newState = msg.state || msg.payload.on;
        const on = G.normaliseState(newState, oldOn);

        if (offTime && on) {
            G.scheduleJob({
                time: offTime, 
                topic: [accountId, buildingId, deviceId].join("."), 
                url: `${G.getApiBaseUrl()}/device/${accountId}/${buildingId}/${deviceId}/off`,
                body: {
                    event: "offTime", 
                    ruleId: new Date().toTimeString() + ">" + offTime.toTimeString(),
                    priority: S.defaults.commandPriorities.timeouts || 2
                }
            });
        }

        const swMsg = {
            accountId, 
            buildingId, 
            topic: deviceId,
            deviceId, 
            api, 
            device, 
            eventId,
            ruleId, 
            delay,
            cause,
            source,
            payload: {
                on,
                hue,
                saturation,
                brightness,
                offTime
            }
        };

        node.status(G.nodeStatus(false, buildingId + ":" + deviceId + " -> " + on));
        msg.payload = "OK";
        msg.statusCode = 200;
        node.send([swMsg, msg]);
    } else {
        genError(404, accountId + "." + buildingId + "." + deviceId, "no such device");
    }
}

function genError(code, id, err) {
    node.status(G.nodeStatus(true, id + ":" + err.toUpperCase()));
    msg.payload = err.toUpperCase();
    msg.statusCode = code;
    node.send([null, msg]);
}

