const [G, moment] = global.get(["globals", "moment"]);
const timeNow = moment().format("HH:mm");

const {event, topic} = msg;
const {eventId = (event || topic)} = msg;
if (eventId) {
    const applyEventToBuilding = building => {
        const buildingRules = building.rules;

        const runRule = ruleId => {
            const rule = buildingRules[ruleId];

            if (rule && !rule.disabled) {
                node.send([{accountId, buildingId, ruleId, rule, eventId, eventData: msg.payload, priority: msg.priority, source}, null, null]);
                node.status({text: timeNow + ": " + ruleId, shape: "dot", fill: "green"});
            } else
                node.status({text: timeNow + ": " + ruleId + " nonextant/disabled", shape: "ring", fill: "red"});
        };

        if (buildingRules) {
            const eventRules = building.eventRules || {};

            // send diagnostics
            node.send([null, null, {payload: {eventId, accountId, buildingId, building, buildingRules, eventRules}}]);

            if (eventRules && eventRules[eventId]) {
                // quickly find rules that apply to the event, and run them in turn
                const rules = eventRules[eventId];

                if (rules && rules.length) {
                    rules.map(rule => runRule(rule));
                }
            } else { // output NO-MATCHING-RULES
                node.send([null, msg, null]);
                node.status({text: "no rules: " + eventId, shape: "ring", fill: "red"});
            }
        } else
            node.warn(`No rules defined in building ${buildingId}`);    
    }    

    const eId = eventId.replace(/\//g, ".")
    const [dfltA, dfltB] = eId.split(".");
    const {accountId = dfltA, buildingId = dfltB, source} = msg
    const building = G.getBuilding(accountId, buildingId);

    if (building) { // great, event applies to one building
        applyEventToBuilding(building)
    } else {
        // event applies to whole account, need to iterate buildings
        G.forBuildingsInAccount(accountId, buildingId => {
            applyEventToBuilding(G.getBuilding(accountId, buildingId))
        })
    }
} else node.warn(`Event id empty, source ${msg.source}`)

