}

node.status({text: msg.api + ": " + msg.id})

// 28-05-2017 Refactored detokenisation; Added notifications; Added "otherwise" actions on rule failure; Added eventData payload; Added raiseevent action
// 05-06-2017 Updated with event & ruleId to support logging
// 09/06/2017 Bug fixed in announcements
// 12/06/2017 delay support in switch actions
// 29-06-2017 accountId/buildingId support
const G = global.get("globals")
const D = global.get("dataEngine")

const moment = global.get("moment");

const {accountId, buildingId, ruleId, rule, factData, eventData, topic} = msg;
const actions = msg.success ? rule.actions : ( rule.otherwise || {} );

const building = G.getBuilding(accountId, buildingId)

for (let actionId in actions) {
    redirectAction(actionId);
}

function redirectAction(actionId) {
    const ruleActions = actions[actionId];

    if (Array.isArray(ruleActions)) {
        switch (actionId) {
            case "store":
                ruleActions.map(action => sendToStorage(action));
                break;

            case "switch":
                ruleActions.map(action => sendSwitch(action));
                break;

            case "announce":
                ruleActions.map(action => sendAnnouncement(action));
                break;

            case "notify":
                ruleActions.map(action => sendNotification(action));
                break;

            case "playsound":
                ruleActions.map(action => sendPlaySound(action));
                break;

            case "raiseevent":
                ruleActions.map(action => sendRaiseEvent(action));
                break;

            default:
                node.warn(ruleId + ":" + actionId + " is not a recognised action");
        }
    } else
        node.warn(ruleId + ": Rule actions not an array");
}


function sendToStorage(storeObj) {
    // 3 types of message - key is mandatory plus either of:
    //      fact: stores a fact value in in the key
    //      value: stores the supplied value in the key
    //      operator & value: stores the value operated on by operator
    const {key, fact, value, operator} = storeObj
    const out = {
        accountId,
        buildingId,
        topic: key
    }

    if (key) {
        if (fact) {
            out.payload = factData[fact];
        } else if (operator && typeof value === "number") {
            const {value: currentValue = 0} = D.getDataNode(accountId, buildingId, key);
            if (operator === "increment") {
                node.send({operator, accountId, buildingId, topic: key, payload: currentValue + value});
            } else {
                node.send({operator, accountId, buildingId, topic: key, payload: currentValue - value});
            }
        } else if (value !== undefined) {
            if (typeof value === "string") {
                out.payload = detokenise(value);
            } else {
                out.payload = value;
            }
        }
    }

    node.status({text: "stored " + key});
    node.send([null, null, null, null, null, out]);
}


function sendSwitch(switchObj) {
    if (!switchObj.disabled) {
        const moment = global.get("moment");

        const msgSwitch = {
            accountId,
            buildingId,
            id: switchObj.id || switchObj.device || switchObj.deviceId,
            api: switchObj.api,
            eventId: topic,
            ruleId,
            delay: switchObj.delay,
            priority: switchObj.priority,
            source: "event",
            duration: switchObj.duration,
            payload: switchObj.state
        };

        node.status({text: msgSwitch.id + ":" + (switchObj.state && switchObj.state.hasOwnProperty("on") ? switchObj.state.on : "unknown") + (switchObj.duration ? ":D" + switchObj.duration : "") + (switchObj.priority ? ":P" + switchObj.priority : "")});
        node.send([msgSwitch, null, null, null, null, null]);
    }
}


function sendAnnouncement(announceObj) {
    const {disabled, message, where, voice, delay} = announceObj;
    // node.warn(message)
    // node.warn(factData)
    if (!disabled) {
        const voiceMsg = detokenise(message);
        const msgAnnounce = {
            accountId,
            buildingId,
            where,
            voice,
            payload: voiceMsg,
            delay
        };

        node.send([null, msgAnnounce, null, null, null, null]);
    }
}


function sendNotification(notifyObj) {
    if (!notifyObj.disabled) {
        if (notifyObj.who && Array.isArray(notifyObj.who)) {
            notifyObj.who.forEach(function(person) {
                let msgNotify = {
                    accountId,
                    who: person,
                    topic: detokenise(notifyObj.subject || notifyObj.message),
                    payload: detokenise(notifyObj.message || notifyObj.subject)
                };

                node.send([null, null, null, msgNotify, null, null]);
            });
        } else
            node.warn(ruleId + ": notify.who section invalid");
    }
}


function sendPlaySound(soundObj) {
    if (!soundObj.disabled) {
        const msgPlaySound = {
            accountId,
            buildingId,
            topic: [accountId, buildingId, "sound", soundObj.where].join("/"),
            payload: soundObj.file || soundObj.sound
        };

        node.send([null, null, null, null, msgPlaySound, null]);
    }
}


function sendRaiseEvent(eventname) {
    const msgRaiseEvent = {
        accountId,
        buildingId,
        topic: eventname,
        payload: eventData || {}
    };

    node.send([null, null, msgRaiseEvent, null, null, null]);
}


function detokenise(message) {
    if (message.indexOf("%") >= 0) {   // it's tokenised
        message = interpolate(message, factData);  // and detokenise it
    }

    return message;
}


function interpolate(templateString, factData) {
    return templateString.replace(/%([^%]*)%/g, (match, key) => {
        const parts = key.split(".");
        const lastPart = G.lastElement(parts);
        // if (!["value", "timestamp"].includes(lastPart))
            // key = key + ".value";
        const value = factData[key + ".value"] || factData[key];
        node.log(value)
        node.log(building.tz)

        if (key.startsWith("time.")) {
            if (key.endsWith(".now.value") || key.endsWith(".localtime.value") || key.endsWith(".sunrise.value") || key.endsWith(".sunset.value"))
                return moment(value).tz(building.timezone).format("H:mm")
            else if (key.endsWith(".untilSunset.value") || key.endsWith(".sinceSunrise.value"))
                return moment.duration(value).humanize(true)
            else if (key.endsWith(".localtime.value"))
                return moment(value).tz(building.timezone).format("H:mm a")
            else if (key.endsWith(".seconds.value") || key.endsWith(".minutes.value"))
                return value;
            else return moment(value).tz(building.timezone).format("dddd, MMMM Do YYYY, H:mm a")
        } else {
            return value;
        }
    });
}

