const objectMatches = (object, filter) =>
    Object.entries(filter).reduce((a, [key, val]) =>
        a && ((object[key] === val) || (typeof val === "boolean" && (object[key] === val || (!object[key] && !val)))), true);

const objectReduce = (object, reduceFn, initialValue) =>
    Object.entries(object).reduce(reduceFn, initialValue);

const buildObjectFromEntries = (object, objFn) =>
    objectReduce(object, (acc, [key, val]) => ({ ...acc, ...objFn([key, val], acc) }), {});

const ensureProp = (obj, prop, dflt = {}) => {
    if (!obj.hasOwnProperty(prop)) obj[prop] = dflt;
    return obj[prop];
};

const G = {
    getApiBaseUrl,
    objectMatches,
    objectReduce,
    buildObjectFromEntries,
    ensureProp,
    deleteSubObjects: function(obj) {
        const tempObj = JSON.parse(JSON.stringify(obj)); // copy object, disconnect from original

        Object.keys(tempObj).forEach(key => {
            if (typeof tempObj[key] === "object") delete tempObj[key];
        });

        return tempObj;
    },
    getBinState: function(oldOn, newState) {
        const stateType = typeof newState;

        if (stateType !== "undefined") {
            if (stateType == "object") {
                if (newState.hasOwnProperty("state") && typeof newState.state === "string")
                    newState = newState.state;
                else
                    return newState.on || false;
            }

            if (stateType == "boolean")
                return newState;

            if (!isNaN(parseInt(newState)) && newState >=0 && newState <=2 )    // newState is numeric
                return [false, true, !oldOn][newState];

            if (stateType === "string") {
                newState = newState.toLowerCase();

                if (newState === "true" || newState === "false") return newState === "true";
                if (newState === "on" || newState === "off") return newState === "on";
            }
        } else return false;
    },
    elapsedSecsSince: (date) => new Date(new Date() - date).getTime() / 1000,
    getAccounts: () => Object.keys(global.get("accounts")),
