    nodeStatus: function(err, text) {
        const colours = err ? {
            fill: "red",
            shape: "ring"
        } : {
            fill: "green",
            shape: "dot"
        }

        return {text, ...colours};
    },
    handleQuery: function(msg, node, opts) {
        var suffix;
        let options = opts || {};
        this.extractParams(msg);

        let account = this.getAccount(msg.accountId);
        if (account) {
            msg.account = account;
            let building = account.buildings[msg.buildingId];
            if (building) {  // building found
                msg.building = building;

                const handleThings = thingType => {
                    const thingId = thingType + "Id";
                    const thingPlural = thingType + "s";
                    suffix = msg[thingId];
                    const thing = building[thingPlural][msg[thingId]];
                    if (thing) {
                        msg[thingType] = thing;
                    } else {
                        msg.error = true;
                        msg.payload = "no such " + thingType + " " + msg[thingId];
                        msg.statusCode = "404";
                    }
                };

                if (msg.deviceId) handleThings("device");
                else if (msg.groupId) handleThings("group");
                else if (msg.sceneId) handleThings("scene");
                else if (msg.sequenceId) handleThings("sequence");
                else if (msg.ruleId) handleThings("rule");
                else if (msg.humanId) handleThings("human");
                else if (msg.personId) handleThings("person");
            } else {
                msg.error = true;
                msg.payload = "no such building " + msg.buildingId;
                msg.statusCode = "404";
            }
        } else {
            msg.error = true;
            msg.payload = "no such account " + msg.accountId;
            msg.statusCode = "404";
        }

        if (!options.noStatus || msg.error) node.status(this.nodeStatus(msg.error, msg.error ? msg.payload : [msg.accountId, msg.buildingId].join(".") + (suffix ? "." + suffix : "")));
    },
    extractParams: function(msg) {
        if (msg.req) Object.keys(msg.req.params).map(key => msg[key] = msg.req.params[key]);
    },
    iterateAllAccounts: function(callback) {
        this.getAccounts().map(accountId => callback(accountId));
    },
    iterateAllBuildings: function(callback) {
        this.getAccounts().map(accountId => {
            this.getBuildings(accountId).map(buildingId => callback(accountId, buildingId));
        });
    },
    forBuildingsInAccount: function(accountId, callback) {
        this.getBuildings(accountId).map(buildingId => callback(buildingId));
