    easySuncalc: function(lat, lon, when) {
        const SunCalc = global.get('suncalc');
        const moment = global.get("moment");

        const now = when || new Date();

        const times = SunCalc.getTimes(now, lat, lon);
        // const position = SunCalc.getPosition(now, lat, lon);

        // position.azimuthDegrees = position.azimuth * (180 / Math.PI);
        // position.altitudeDegrees = position.altitude * (180 / Math.PI);
        // position.bearingDegress = (position.azimuthDegrees + 180) % 360;
        // position.azimuthCompass = this.compassDirection(position.bearingDegrees);

        Object.defineProperty(times, "isDawn", {
            get: function() {
                return moment(now).isBetween(this.dawn, this.sunrise, "minutes");
            },
            enumerable: true
        });

        Object.defineProperty(times, "isDusk", {
            get: function() {
                return moment(now).isBetween(this.dusk, this.nauticalDusk, "minutes");
            },
            enumerable: true
        });

        Object.defineProperty(times, "isMorningGoldenHour", {
            get: function() {
                return moment(now).isBetween(this.dawn, this.goldenHourEnd, "minutes");
            },
            enumerable: true
        });

        Object.defineProperty(times, "isEveningGoldenHour", {
            get: function() {
                return moment(now).isBetween(this.goldenHour, this.night, "minutes");
            },
            enumerable: true
        });

        Object.defineProperty(times, "isGoldenHour", {
            get: function() {
                return this.isMorningGoldenHour || this.isEveningGoldenHour;
            },
            enumerable: true
        });

        Object.defineProperty(times, "isDaylight", {
            get: function() {
                return moment(now).isBetween(this.goldenHourEnd, this.goldenHour, "minutes");
            },
            enumerable: true
        });

        Object.defineProperty(times, "isNight", {
            get: function() {
                return moment(now).isAfter(this.nauticalDusk, "minutes") || moment(now).isBefore(this.nauticalDawn, "minutes");
            },
            enumerable: true
        });

        Object.defineProperty(times, "daylightDuration", {
            get: function() {
                return moment(this.sunset).diff(this.sunrise);
            },
            enumerable: true
        });

        Object.defineProperty(times, "sinceSunrise", {
            get: function() {
                return moment(now).diff(this.sunrise);
            },
            enumerable: true
        });

        Object.defineProperty(times, "untilSunset", {
            get: function() {
                return moment(this.sunset).diff(now);
            },
            enumerable: true
        });

        const suncalc = {
            times: times,
            // position: position,
        };

        suncalc.times.now = now;

        return suncalc;
    },
