    ruleHasConditions: function(rule) {
        return (
            rule.conditions &&
            ((rule.conditions.all && Array.isArray(rule.conditions.all) && rule.conditions.all.length) ||
            (rule.conditions.any && Array.isArray(rule.conditions.any) && rule.conditions.any.length))
        );
    },
    expandConditions: function(condBlock) {
        if (condBlock.all && Array.isArray(condBlock.all) && condBlock.all.length) {
            this.expandConditionsRecursive(condBlock.all, this);
        }

        if (condBlock.any && Array.isArray(condBlock.any) && condBlock.any.length) {
            this.expandConditionsRecursive(condBlock.any, this);
        }
    },
    expandConditionsRecursive: function(allOrAnyBlock, self) {
        const lastPartIsOneOf = (thing, values, sep) => {
            const parts = thing.split(sep);
            const lastPart = parts[parts.length - 1];
            return values.includes(lastPart);
        }

        allOrAnyBlock.forEach(function(element) {
            if (element.all && Array.isArray(element.all) && element.all.length) {
                // recurse down into nested all blocks
                self.expandConditionsRecursive(element.all, self);
            } else if (element.any && Array.isArray(element.any) && element.any.length) {            // recurse down into nested any blocks
                self.expandConditionsRecursive(element.any, self);
            } else if (element.fact) {
                if (!lastPartIsOneOf(element.fact, ["previousValue", "value", "timestamp", "previousTimestamp"], "."))
                    element.fact = element.fact + ".value";
            }
        });
    },
    formatDate: function(date, format) { return global.get("moment")(date).format(format) },
    shortestDateTime: function(date = Date.now()) { return this.formatDate(date, "D-MMM HH:mm") },
    compassDirection: function(angle) {
        return ["N", "NNE", "NE", "ENE", "E", "ESE", "SE", "SSE", "S", "SSW", "SW", "WSW", "W", "WNW", "NW", "NNW"][Math.floor((angle / 22.5) + 0.5) % 16];
    },
    genHttpError: (node, msg, code, id, err) => {
