    deviceDescriptionSortComparator: (a, b) => (a.description || "").toLowerCase() < (b.description || "").toLowerCase() ? -1 : ((b.description || "").toLowerCase() < (a.description || "").toLowerCase()) ? 1 : 0,
    convertODataType: (type, value) => ((!type || ["Edm.String", "Edm.Binary", "Edm.Guid", "Edm.Boolean"].includes(type)) ? value : (["Edm.Int32", "Edm.Int64", "Edm.Double"].includes(type) ? Number(value) : (type ==  "Edm.DateTime" ? new Date(value) : null))),
    objectFromEntries: keyValueArray => keyValueArray.map(([key, val]) => ([key, val])).reduce((obj, [k, v]) => Object.assign(obj, { [k]: v }), {}),
    newObjectFromArray: array => array.reduce((a, c) => {a[c] = {}; return a;}, {}),
    invertedTicks: () => (new Date(9999, 12, 31, 23, 59, 59, 9999999).getTime() - new Date().getTime()),
    toTitleCase: str => str.replace(/\w\S*/g, txt => txt.charAt(0).toUpperCase() + txt.substr(1).toLowerCase()),
    mean: (...args) => args.reduce((a, b) => a + b) / args.length,
    lastElement: array => array[array.length - 1],
    renameKey: function(object, key, newKey) {
        if (typeof object === "object" && object.hasOwnProperty(key)) {
            object[newKey] = object[key];
            delete object[key];
        } else node.warn("renameKey: no such key or not an object");
    },
