    stateWord: binState => binState ? "on" : "off",
    isActuator: (device) => (device.actuator || device.light || device.heating || device.cooling || device.appliance || device.wol || device.virtual || device.wemoEmulation || device.readonly || device.alwaysOn) && !(device.stateless || device.hidden),
    findPersonByFullName: function(accountId, fullName) {
        const account = this.getAccount(accountId);
        const compName = fullName.toLowerCase();
        const personKey = Object.keys(account.people).find(personId => {
            const person = account.people[personId];
            return ([person.firstName, person.lastName].join(" ").trim().toLowerCase() === compName);
        });
        if (personKey)
            return Object.assign({personId: personKey}, account.people[personKey]);
        else
            return {};
    },
    copyProperty: (obj1, obj2, propName) => Object.defineProperty(obj2, propName, Object.getOwnPropertyDescriptor(obj1, propName)),
    shallowCopy: obj => Object.keys(obj).reduce((a, key) => typeof obj[key] === "object" ? a : {...a, [key]: obj[key]}, {}),
    bind: (object, method) => object[method] = object[method].bind(object),
    bindAll: object => Object.keys(object).filter(member => typeof member === "function").map(member => bind(object, member)),
    memoizeResult: (fn, name) => {
        const r = global.get(name);

        if (r === undefined) {
            const x = fn();
            global.set(name, x);
            // node.warn("new");
            return x;
        } else {
            // node.warn("cache");
            return r;
        }
    },
    normaliseState: (newState, oldOn = false) => {
        switch (typeof newState) {
            case "boolean":
                return newState

            case "string":
                newState = newState.toLowerCase()
                if (newState === "on" || newState === "1")
                     return true
                else if (newState === "off" || newState === "0")
                    return false
                else if (newState === "toggle" || newState === "2")
                    return !oldOn
                else {
                    node.warn(`Invalid state string ${newState}`)
                    return
                }
                break;

            case "number":
                if (newState === "1")
                     return true
                else if (newState === "0")
                    return false
                else if (newState === "2")
                    return !oldOn
                else {
                    node.warn(`Invalid state number ${newState}`)
                    return
                }
                break;

            default:
                node.warn(`Invalid state type ${typeof newState}`)
        }
    }
};

