if (accountId && buildingId && rule && ruleId && eventId && factData && typeof factData === "object") {
    const moment = global.get("moment");
    const Engine = global.get("rulesEngine");
    const engine = new Engine();
    const customFacts = (rule.conditions.all || rule.conditions.any).filter(condition => ["afterTime", "beforeTime", "elapsedGreaterThanInclusive", "elapsedGreaterThan", "elapsedLessThanInclusive", "elapsedLessThan"].includes(condition.operator)).length > 0

    rule.event = { type: "ruleSuccess" };

    engine.addRule(rule);

    engine.addOperator("afterTime", (factValue, jsonValue) => {
        const today = moment().format("YYYY-MM-DD");
        const jsonValueMoment = moment(today + " " + jsonValue);
        if (!jsonValueMoment.isValid()) {
            node.warn("afterTime failed: " + jsonValue + "/" + factValue);
            return false;
        }
        const retVal = moment(factValue).isAfter(jsonValueMoment);
        return retVal;
    });

    engine.addOperator("beforeTime", (factValue, jsonValue) => {
        const today = moment().format("YYYY-MM-DD");
        const jsonValueMoment = moment(today + " " + jsonValue);
        if (!jsonValueMoment.isValid()) {
            node.warn("beforeTime failed: " + jsonValue + "/" + factValue);
            return false;
        }
        const retVal = moment(factValue).isBefore(jsonValueMoment);
        return retVal;
    });

    engine.addOperator("elapsedGreaterThanInclusive", (factValue, jsonValue) => {
        const now = moment();
        const then = moment(factValue);
        const calcVal = now.diff(then, "seconds") / 60;
        const retVal = calcVal >= jsonValue;
        // node.warn(`${now} - ${then} = ${calcVal} >= ${jsonValue} == ${retVal}`)
        return retVal;
    });

    engine.addOperator("elapsedGreaterThan", (factValue, jsonValue) => {
        const calcVal = moment().diff(moment(factValue), "seconds") / 60;
        const retVal = calcVal > jsonValue;
        // node.log("elapsedGreaterThan: " + calcVal + " > " + jsonValue + " = " + retVal);
        return retVal;
    });

    engine.addOperator("elapsedLessThanInclusive", (factValue, jsonValue) => {
        const calcVal = moment().diff(moment(factValue), "seconds") / 60;
        const retVal = calcVal <= jsonValue;
        // node.log("elapsedLessThanInclusive: " + calcVal + " <= " + jsonValue + " = " + retVal);
        return retVal;
    });

    engine.addOperator("elapsedLessThan", (factValue, jsonValue) => {
        const calcVal = moment().diff(moment(factValue), "seconds") / 60;
        const retVal = calcVal < jsonValue;
        // node.log("elapsedLessThan: " + calcVal + " < " + jsonValue + " = " + retVal);
        return retVal;
    });

    engine.on("success", (ruleEvent, almanac, ruleResult) => {
        node.status({text: "Rule " + ruleId + " = TRUE", shape: "dot", fill: "green"});
        const msgLog = {
            topic: [accountId, buildingId, "logging", "events", ruleId].join("/"),
            eventId,
            success: true,
            accountId,
            buildingId,
            ruleResult,
            source,
            rule,
            factData
        };

        node.send([{
            success: true,
            accountId,
            buildingId,
            eventId,
            ruleId,
            source,
            rule,
            factData,
            status: ruleEvent,
            ruleResult
        }, null, null, msgLog]);
    });

    engine.on("failure", (ruleEvent, almanac, ruleResult) => {
        node.status({text: "Rule " + ruleId + " = FALSE", shape: "ring", fill: "red"});
        const msgLog = {
            topic: [accountId, buildingId, "logging", "events", ruleId].join("/"),
            payload: {
                eventId,
                success: false,
                buildingId,
                ruleResult,
                rule,
                factData,
                source
            }
        };

        node.send([null, {
            success: false,
            accountId,
            buildingId,
            eventId,
            ruleId,
            rule,
            source,
            factData,
            status: ruleEvent,
            ruleResult
        }, null, msgLog]);
    });

    engine.run({...factData}).catch(error => {
        node.send([null, null, {
            root: rule.conditions.any ? "any" : "all",
            factCount: Object.keys(factData).length,
            factIds: Object.keys(factData),
            customFacts,
            error,
            accountId,
            buildingId,
            source,
            eventId,
            ruleId,
            rule,
            factData
        }, null]);
    });
} else node.warn(`Something is missing for rule ${ruleId} event ${eventId}`)

