let movementLog = global.get("movementLog");

if (movementLog) {
    for (let accountId in movementLog) {
        for (let buildingId in movementLog[accountId]) {
            for (let roomId in movementLog[accountId][buildingId]) {
                let now = new Date();
                let sinceLastRoom, sinceLastAnywhere;
                let sinceLastRoomMins, sinceLastAnywhereMins;

                if (!movementLog[accountId][buildingId][roomId].last) {
                    sinceLastRoomMins = null;
                    sinceLastRoom = "unknown";
                } else {
                    let interval = new Date(now - movementLog[accountId][buildingId][roomId].last).getTime();
                    let duration = moment.duration(interval);
                    sinceLastRoomMins = duration.humanize();
                    sinceLastRoom = interval;
                }

                if (!movementLog[accountId][buildingId].anywhere) movementLog[accountId][buildingId].anywhere = {};

                if (!movementLog[accountId][buildingId].anywhere.last)
                    sinceLastAnywhere = "unknown";
                else {
                    let interval = new Date(now - movementLog[accountId][buildingId].anywhere.last).getTime();
                    let duration = moment.duration(interval);
                    sinceLastAnywhereMins = duration.humanize();
                    sinceLastAnywhere = interval;
                }

                movementLog[accountId][buildingId].anywhere.sinceLast = sinceLastAnywhere;
                movementLog[accountId][buildingId].anywhere.sinceLastMins = sinceLastAnywhereMins;

                movementLog[accountId][buildingId][roomId].sinceLast = sinceLastRoom;
                movementLog[accountId][buildingId][roomId].sinceLastMins = sinceLastRoomMins;
            }
        }
    }

    global.set("movementLog", movementLog);

    return {payload: movementLog};
}

