    if (!global.get("store")) global.set("store", _createStorage({}));

    return global.get("store");
}

const [G = {}, server = {}, changeThresholds = {}, M = {}] = global.get(["globals", "server", "changeThresholds", "metrics"]);

const _mean = args => args.reduce((a, b) => a + b, 0) / args.length;
const _historyMean = args => args.reduce((a, b) => a + b.value, 0) / args.length;
const _round = (number, precision = 2) => {
    const factor = Math.pow(10, precision);
    return Math.round(number * factor) / factor;
};
const _createIfNotExists = (object, property, value) => {
    if (object && !object.hasOwnProperty(property)) object[property] = value;
};
const _getEMA = (array, range) => array.reduce((prev, curr, i) => i ? prev.concat({ value: _round(2 * curr.value / (range + 1) + prev[prev.length - 1].value * (range - 1) / (range + 1), 2), timestamp: curr.timestamp }) : prev, [{ value: array[0].value, timestamp: array[0].timestamp }]);
const _meanRateOfChangePerMin = array => array.reduce((p, c, i) => i ? p + ((c.value - array[i - 1].value) / ((c.timestamp - array[i - 1].timestamp) / 60000)) : p, 0) / (array.length - 1);
const _range = (array, property) => array[array.length - 1][property] - array[0][property];
const _timespan = array => _range(array, "timestamp");
const _deltaPerHour = array => _range(array, "value") * (60 / (_timespan(array) / 60000));
const _ratePerHour = array => array.length * (60 / ((_timespan(array)) / 1000 / 60));
const _isNumber = x => typeof x === "number" || (!["object", "boolean"].includes(typeof x) && !Number.isNaN(parseFloat(x)) && Number.isNaN(Date.parse(x)) && x.match(/^[0-9]+([.|,][0-9]+)?$/) !== null);

const storeData = (props, timestamp, callback) => {
    const S = getStore();
    const now = timestamp || Date.now();
    let events = [];

    const extractFullKey = (key) => {
        const parts = key.split(".");
        return {
            accountId: parts[0],
            buildingId: parts[1],
            key: parts.slice(2).join(".")
        };
    };

    if (props.hasOwnProperty("key") && (props.hasOwnProperty("value") || (props.hasOwnProperty("values") && typeof props.values == "object"))) {
        let accountId, buildingId;
        let key = props.key;
        let insertionPoint;

        if (props.hasOwnProperty("accountId")) {
            accountId = props.accountId;

            if (props.hasOwnProperty("buildingId")) {
                buildingId = props.buildingId;
            }
        }

        if ((buildingId && !accountId) || !(buildingId || accountId)) {
            // Assume accountId & buildingId are to be extracted from key
            let newBag = extractFullKey(props.key);

            accountId = newBag.accountId;
            buildingId = newBag.buildingId;
            key = newBag.key; // the trimmed down key
        }

        if (S.hasOwnProperty(accountId)) { // account exists
            insertionPoint = S[accountId]; // this is where we'll put the data

            if (buildingId && S[accountId].hasOwnProperty(buildingId)) { // building exists
                insertionPoint = S[accountId][buildingId]; // this is where we'll put the data
            } else buildingId = null;
        } else accountId = null;

        if ((accountId && buildingId && key) || (accountId && key)) {
            if (props.hasOwnProperty("values") && typeof props.values == "object") { // multiple values to store
                Object.keys(props.values).map(valKey => {
                    let fqKey = [key, valKey].join(".");

                    let options = {
                        timestamp: now,
                        metricMeta: props.metricMeta,
                        callback: callback
                    };
                    events = [].concat(events, _ingestData(accountId, buildingId, insertionPoint, fqKey, props.values[valKey], options));
                });

                success = true;
            } else if (props.hasOwnProperty("value")) {
                let options = {
                    timestamp: now,
                    metricMeta: props.metricMeta,
                    callback: callback
                };
                events = _ingestData(accountId, buildingId, insertionPoint, key, props.value, options);
            }
        }
    }

    return events;
};

const _ingestData = (accountId, buildingId, object, key, value, options) => {
    let keyParts = key.split(".");
    let metricId = keyParts[keyParts.length - 1];
    let metricMeta = options.metricMeta || M[metricId] || M.default || {};
    let events = [];

    if (value !== undefined) {
        while (metricMeta.map) {
            keyParts[keyParts.length - 1] = metricMeta.map;
            metricId = metricMeta.map;
            metricMeta = M[metricId] || M.default || {};
            key = keyParts.join(".");
        }

        let isNumericData = _isNumber(value) || (!["boolean", "object"].includes(typeof value) && !metricMeta.noStatistics);

        _createIfNotExists(object, key, {});

        if (isNumericData) {
            const {decimals = 2} = metricMeta;
            if (!metricMeta.noRounding) value = _round(value, decimals);

            if (!metricMeta.noEvents) {
                _createIfNotExists(object[key], "eventValues", {
                    value: null,
                    mean: null,
                    max: null,
                    min: null
                });
            }
        }

        if (!metricMeta.noEvents && ((!isNumericData && object[key].value != value) || (isNumericData && (object[key].eventValues.value === null || Math.abs(value - object[key].eventValues.value) >= (changeThresholds[metricId] || changeThresholds.any))))) {
            events.push(_generateEvent(accountId, buildingId, value, object[key], key, "changed"));
            if (isNumericData) object[key].eventValues.value = value;
        }

        if (isNumericData && !metricMeta.noEvents) {
            if (value === 0 && object[key].value !== 0) {
                events.push(_generateEvent(accountId, buildingId, value, object[key], key, "zero"));
            } else if (value !== 0 && object[key].value === 0) {
                events.push(_generateEvent(accountId, buildingId, value, object[key], key, "nonzero"));
            }
        }

        if (isNumericData && !metricMeta.noStatistics) {
            object[key].delta = value - _round(object[key].value, 2);

            const oldMax = object[key].maxAll || value;
            const newMax = Math.max(oldMax, value);
            object[key].maxAll = newMax;
            if (!metricMeta.noEvents && newMax !== oldMax)
                events.push(_generateEvent(accountId, buildingId, newMax, object[key], key, "maxAll", "changed"));

            const oldMin = object[key].minAll || value;
            const newMin = Math.min(oldMin, value);
            object[key].minAll = newMin;
            if (!metricMeta.noEvents && newMin !== oldMin)
                events.push(_generateEvent(accountId, buildingId, newMin, object[key], key, "minAll", "changed"));
        }

        object[key].previousValue = object[key].value;
        object[key].value = value;
        // object[key].numeric = isNumericData

        events.push(_generateEvent(accountId, buildingId, value, object[key], key, "data"));

        if (options.callback && typeof options.callback === "function")
            options.callback(key, value, options.timestamp)

        if (!metricMeta.noTimestamp) {
            object[key].previousTimestamp = object[key].timestamp;
            object[key].timestamp = options.timestamp;
        }

        if (!metricMeta.noHistory) {
            _createIfNotExists(object[key], "history", []);
            _addHistory(object[key].history, value, options.timestamp);

            object[key].rate = _round(_ratePerHour(object[key].history), 2);
            object[key].timespan = _timespan(object[key].history);

            if (!metricMeta.noStatistics && isNumericData) {
                let tempVal = _round(_historyMean(object[key].history), metricMeta.decimals);
                if (!metricMeta.noEvents && (object[key].eventValues.mean === null || Math.abs(tempVal - object[key].eventValues.mean) >= (changeThresholds[metricId] || changeThresholds.any))) {
                    events.push(_generateEvent(accountId, buildingId, tempVal, object[key], key, "mean", "changed"));
                    object[key].eventValues.mean = tempVal;
                }
                object[key].mean = tempVal;

                tempVal = Math.max(...object[key].history.map(ele => ele.value));
                if (!metricMeta.noEvents && (object[key].eventValues.max === null || Math.abs(tempVal - object[key].eventValues.max) >= (changeThresholds[metricId] || changeThresholds.any))) {
                    events.push(_generateEvent(accountId, buildingId, tempVal, object[key], key, "max", "changed"));
                    object[key].eventValues.max = tempVal;
                }
                object[key].max = tempVal;

                tempVal = Math.min(...object[key].history.map(ele => ele.value));
                if (!metricMeta.noEvents && (object[key].eventValues.min === null || Math.abs(tempVal - object[key].eventValues.min) >= (changeThresholds[metricId] || changeThresholds.any))) {
                    events.push(_generateEvent(accountId, buildingId, tempVal, object[key], key, "min", "changed"));
                    object[key].eventValues.min = tempVal;
                }
                object[key].min = tempVal;

                if (!metricMeta.noEvents) {
                    object[key].roc = _round(_meanRateOfChangePerMin(object[key].history), 2);
                    object[key].cph = _round(_deltaPerHour(object[key].history), 2);
                    object[key].ma3 = _getEMA(object[key].history, 3);
                    object[key].ma6 = _getEMA(object[key].history, 6);
                }
            }
        }

        if (metricMeta.convert && !options.noRecurse) {
            const _arithmetic = global.get("arithmetic");

            metricMeta.convert.map(conversion => {
                const toMetric = Object.keys(conversion)[0];
                const newKey = key.replace(metricId, toMetric);
                const formula = Object.values(conversion)[0].replace(metricId, value);
                const converted = _arithmetic(formula);
                events = [].concat(events, _ingestData(accountId, buildingId, object, newKey, converted, { timestamp: options.timestamp, noRecurse: true }));
            });
        }
    }

    return events;
};


const store = () => getStore();

const loadStorage = () => {
    loadFromBlob()
        .then(results => {
            const S = JSON.parse(results);
            global.set("store", S);
            node.log("dataEngine loaded from blob storage")
            return S;
        })
        .catch(e => node.error(e));
};

const _filterPropertiesByPrefix = (object, prefix) =>
    Object.keys(object)
    .reduce((filtered, key) => {
        if (key.startsWith(prefix))
            filtered[key] = object[key];

        return filtered;
    }, {});

const getAccountData = accountId => {
    const S = getStore();
    return (accountId && S.hasOwnProperty(accountId)) ? S[accountId] : undefined;
};

const getBuildingData = (accountId, buildingId) => {
    const S = getStore();
    if (accountId && S.hasOwnProperty(accountId) && buildingId && S[accountId].hasOwnProperty(buildingId)) {
        return S[accountId][buildingId];
    } else
        return undefined;
};

const getRoomData = (accountId, buildingId, roomId) => {
    const S = getStore();
    if (accountId && S.hasOwnProperty(accountId) && buildingId && S[accountId].hasOwnProperty(buildingId) && roomId) {
        let keyPrefix = [roomId, ""].join(".");
        return _filterPropertiesByPrefix(S[accountId][buildingId], keyPrefix);
    } else
        return undefined;
};

const getBuildingMetricData = (accountId, buildingId, metricId) => {
    const S = getStore();
    if (accountId && S.hasOwnProperty(accountId) && buildingId && S[accountId].hasOwnProperty(buildingId) && metricId) {
        let keySuffix = ["", metricId].join(".");
        return _filterPropertiesBySuffix(S[accountId][buildingId], keySuffix);
    } else
        return undefined;
};

const getRoomMetricData = (accountId, buildingId, roomId, metricId) => {
    const S = getStore();
    if (accountId && S.hasOwnProperty(accountId) && buildingId && S[accountId].hasOwnProperty(buildingId) && roomId && metricId) {
        const keyPrefix = [roomId, ""].join(".");
        const keySuffix = ["", metricId].join(".");
        return _filterPropertiesByPrefixAndSuffix(S[accountId][buildingId], keyPrefix, keySuffix);
    } else
        return undefined;
};

const getRoomMetricValues = (accountId, buildingId, roomId, metricId) => {
    const data = getRoomMetricData(accountId, buildingId, roomId, metricId);

    if (data)
        return Object.keys(data).map(key => data[key].value);
    else
        return data;
};

const getRoomMetricMean = (accountId, buildingId, roomId, metricId) => _mean(getRoomMetricValues(accountId, buildingId, roomId, metricId));

const getDeviceData = (accountId, buildingId, roomId, deviceId) => {
    const buildingData = _filterPropertiesByPrefix(getBuildingData(accountId, buildingId), [roomId, deviceId, ""].join("."));
    return Object.keys(buildingData).map(key => Object.assign({key: key}, buildingData[key]));
};

const getDeviceDataValues = (accountId, buildingId, roomId, deviceId) => {
    const buildingData = _filterPropertiesByPrefix(getBuildingData(accountId, buildingId), [deviceId, ""].join("."));
    return Object.assign({}, ...Object.keys(buildingData).map(key => {
        const newObj = {};
        const parts = key.split(".");
        const metric = parts[parts.length - 1];
        newObj[metric] = buildingData[key].value;
        if (metric == "on" && buildingData[key].timestamp) newObj.timestamp = buildingData[key].timestamp;
        return newObj;
    }));
};

const _filterPropertiesBySuffix = (object, suffix) =>
    Object.keys(object)
    .reduce((filtered, key) => {
        if (key.endsWith(suffix))
            filtered[key] = object[key];

        return filtered;
    }, {});

const _filterPropertiesByPrefixAndSuffix = (object, prefix, suffix) =>
    Object.keys(object)
    .reduce((filtered, key) => {
        if (key.startsWith(prefix) && key.endsWith(suffix))
            filtered[key] = object[key];

        return filtered;
    }, {});

const _getAccountOrBuilding = (accountId, buildingId) => {
    const S = getStore();
    if (accountId && buildingId)
        if (S && S[accountId] && S[accountId][buildingId])
            return S[accountId][buildingId];
        else return undefined;
    else if (accountId)
        if (S && S[accountId])
            return S[accountId];
        else return undefined;
    else return undefined;
};

const getDataNode = (accountId, buildingId, key) => {
    const object = _getAccountOrBuilding(accountId, buildingId);

    if (object) {
        if (Array.isArray(key)) key = key.join(".");

        if (object[key])
            return object[key];
        else
            return undefined;
    }
};

const getFilteredData = (accountId, buildingId, keyParts) => {
    const S = getStore();
    let object;

    if (accountId && buildingId) {
        try {
            object = S[accountId][buildingId];
        } catch (e) {
            return undefined;
        }
    } else if (accountId) {
        try {
            object = S[accountId];
        } catch (e) {
            return undefined;
        }
    }

    return getFilteredObjectData(object, keyParts);
};

const getFilteredObjectData = (object, keyParts) => {
    keyParts.push("");
    const keyPrefix = keyParts.join(".");
    return _filterPropertiesByPrefix(object, keyPrefix);
};

const _removeExpiredHistory = (history, now) => {
    while (history.length > 0 && (now || Date.now()) - history[0].timestamp > server.defaults.maxHistoryAgeMillis) {
        history.shift();
    }
};

const _addHistory = (history, value, timestamp) => {
    _removeExpiredHistory(history, timestamp);

    if (value !== null) {
        history.push({
            value: value,
            timestamp: timestamp || Date.now()
        });
    }
};

const _generateEvent = (accountId, buildingId, value, data, ...parts) => {
    let event = {
        accountId: accountId,
        buildingId: buildingId,
        event: parts.join("."),
        value: value,
        data: data
    };

    // G.rulesEngine(event);
    return event;
};

const renameKey = (accountId, buildingId, oldKey, newKey) => {
    const S = getStore();
    if (S && S[accountId] && S[accountId][buildingId]) {
        G.renameKey(S[accountId][buildingId], oldKey, newKey);
    } else {
        node.warn("dataEngine.renameKey: no such object");
    }
};

const decomposeStoreKey = (accountId, buildingId, key) => {
    const returnObj = {};
    const parts = key.split(".");
    const partMap = (parts.length === 3) ? {roomIdx: 0, deviceIdx: 1, metricIdx: 2} : {roomIdx: 0, metricIdx: 1};
    const building = G.getBuilding(accountId, buildingId);

    const roomId = parts[partMap.roomIdx];
    returnObj.room = (building && building.rooms && building.rooms[roomId] && building.rooms[roomId].label) ? building.rooms[roomId].label : G.toTitleCase(roomId);

    if (partMap.deviceIdx) {
        const deviceId = parts[partMap.roomIdx] + "." + parts[partMap.deviceIdx];
        returnObj.deviceName = (building && building.devices && building.devices[deviceId] && building.devices[deviceId].description) ? building.devices[deviceId].description : G.toTitleCase(deviceId);
    }

    const metricId = parts[partMap.metricIdx];
    if (M && M[metricId]) {
        returnObj.metricName = M[metricId].label ? M[metricId].label : G.toTitleCase(metricId);
        returnObj.unitName = M[metricId].unit ? M[metricId].unit : null;
    }
    return returnObj;
};

const calculateFact = (accountId, buildingId, fact) => {
    if (fact.startsWith("time.")) {
        const moment = global.get("moment");
        const {timezone, lat, lon} = G.getBuilding(accountId, buildingId);

        switch (fact) {
            case "time.now":
                return new Date();

            case "time.localTime":
                return moment().tz(timezone).format();

            case "time.dow":
                return moment().tz(timezone).format("dddd");

            case "time.seconds":
                return (new Date()).getSeconds();

            case "time.minutes":
                return (new Date()).getMinutes();

            case "time.weekend":
                return ["Saturday", "Sunday"].includes(moment().tz(timezone).format("dddd"));

            case "time.weekday":
                return !["Saturday", "Sunday"].includes(moment().tz(timezone).format("dddd"));

            default:
                const sunTimes = G.easySuncalc(lat, lon).times;
                const [lastPart] = fact.split(".").reverse();
                const propName = lastPart + ".value";

                if (sunTimes.hasOwnProperty(lastPart))
                    return sunTimes[lastPart];
        }
    }

    return undefined;
};

const getFactValues = (accountId, buildingId, rule, eventData) => {
    const factIds = rule.facts && Array.isArray(rule.facts) ? rule.facts : [];
    return factIds.reduce((facts, factId) => {
        const val = factId.startsWith("eventData") ? eventData : getFactValue(accountId, buildingId, factId);
        if (val === null) node.warn(`Null value for ${factId} used in rule`);
        facts[factId] = val;
        return facts;
    }, {})
};

const getFactValue = (accountId, buildingId, fact) => {
    const parts = fact.split(".");
    const lastPart = parts[parts.length - 1];

    let variable = "value";
    if (["previousValue", "value", "timestamp", "previousTimestamp"].includes(lastPart)) {
        variable = lastPart;
        parts.splice(-1);
    }

    const factRoot = parts.join(".");

    let value = calculateFact(
        accountId,
        buildingId,
        factRoot
    );

    if (value === undefined) {
    // node.warn({a: accountId, b: buildingId, f: fact, v: value, factRoot});
        const values = getDataNode(
            accountId,
            buildingId,
            factRoot
        );
        if (values) value = values[variable];
    }

    return value === undefined ? null : value;
};

const saveToBlob = () => {
    const S = getStore();
    const blobContainer = "bobb-data-engine"
    const accountSegment = server.accountSegment   // account segments allow a server to handle buildings for just a subset of accounts, e.g. UK South
    const blobSegment = accountSegment ? accountSegment + "/" : ""
    const blobName = "bobbDataEngine.json"

    G.createBlobContainer(blobContainer)
        .then(result => {
            node.log("dataEngine persisted to blob storage")
            return G.saveBlob(blobContainer, blobSegment + blobName, JSON.stringify(S))
        })
        .then(result => node.log(result))
        .catch(e => node.error(e))
};

const loadFromBlob = () => {
    const blobContainer = "bobb-data-engine"
    const accountSegment = server.accountSegment   // account segments allow a server to handle buildings for just a subset of accounts, e.g. UK South
    const blobSegment = accountSegment ? accountSegment + "/" : ""
    const blobName = "bobbDataEngine.json"

    return G.loadBlob(blobContainer, blobSegment + blobName)
};

const deleteInvalidData = () => {
    const S = getStore();
    Object.keys(S).map(accountId => 
        Object.keys(S[accountId]).map(buildingId => {
            Object.keys(S[accountId][buildingId]).filter(key => key.includes("/")).map(key => {
                node.log(`Deleting key ${key} from ${accountId}.${buildingId}`)
                delete S[accountId][buildingId][key]
            })
            Object.keys(S[accountId][buildingId]).filter(key => key.includes([accountId, buildingId].join("."))).map(key => {
                node.log(`Deleting key ${key} from ${accountId}.${buildingId}`)
                delete S[accountId][buildingId][key]
            })
            Object.keys(S[accountId][buildingId]).filter(key => key.match(/presence\.people\.\d{1,3}/) !== null).map(key => {
                node.log(`Deleting key ${key} from ${accountId}.${buildingId}`)
                delete S[accountId][buildingId][key]
            })
            Object.keys(S[accountId][buildingId]).filter(key => (key === "presence..present" || key.match(/^(\w+)\.+\1\b/) !== null || key.startsWith("test.sensor") || key.endsWith(".state") || key.endsWith(".unknown") || key.endsWith(".offTime") || key.endsWith(".timestamp"))).map(key => {
                node.log(`Deleting key ${key} from ${accountId}.${buildingId}`)
                delete S[accountId][buildingId][key]
            })
            Object.keys(S[accountId][buildingId]).filter(key => key.endsWith(".icon")).map(key => {
                node.log(`Deleting key ${key} from ${accountId}.${buildingId}`)
                delete S[accountId][buildingId][key]
            })
            Object.keys(S[accountId][buildingId]).filter(key => key.includes(".co2sensor.")).map(key => {
                node.log(`Deleting key ${key} from ${accountId}.${buildingId}`)
                delete S[accountId][buildingId][key]
            })
        })
    )
};

const dataEngine = {
    getStore,
    storeData,
    store,
