const G = global.get("globals");
const D = global.get("dataEngine");
const parts = msg.topic.split("/");

const accountId = parts[0];
const buildingId = parts[1];
const lastPart = parts[parts.length - 1];

const pay = msg.payload;

// 5 part topic for sonoff5 = account/building/sonoff/deviceId/STATE - need to derive room from device def
switch (lastPart) {
    case "SENSOR":
        const deviceId = parts[3];

        if (deviceId) {
            node.send([{
                topic: [accountId, buildingId, deviceId, "power"].join("."),
                payload: msg.payload.ENERGY,
                source: "sonoff"
            }, null]);
        } else {
            node.warn(`no deviceId in ${topic}/${msg}`)
        }
        break;

    case "STATE": {
        let deviceTopic = parts[3];

        if (pay.hasOwnProperty("POWER1") || pay.hasOwnProperty("POWER2")) { // sonoff duals
            for (let i = 1; i <= 2; i++) {
                const deviceId = findDeviceByTopicAndChannel(deviceTopic, i);
                const itsOn = (pay["POWER" + i] === "ON");

                if (deviceId)
                    node.send([{
                        topic: [accountId, buildingId, deviceId, "on"].join("."),
                        payload: itsOn,
                        source: "sonoff"
                    },
                    null]);
                else
                    node.warn(`Couldn't locate device for topic ${deviceTopic} channel ${i}`);
            }
        } else {
            const deviceId = parts[3];
            const device = G.getDevice(accountId, buildingId, deviceId);
            const itsOn = (pay.POWER === "ON");

            if (device) {
                node.send([{
                    topic: [accountId, buildingId, deviceId, "on"].join("."),
                    payload: itsOn,
                    source: "sonoff"
                }, null]);
            }
        }
        break;
    }

    case "RESULT": {
        const powerProp = Object.keys(pay)[0];
        const itsOn = (pay[powerProp] == "ON");
        const channel = Number(powerProp.substr(-1, 1));
        let deviceId = parts[3];

        if (channel > 0) { // sonoff duals
            deviceId = findDeviceByTopicAndChannel(deviceId, channel);
        }

        const device = G.getDevice(accountId, buildingId, deviceId);

        if (device) {
            node.send([{
                topic: [accountId, buildingId, deviceId, "on"].join("."),
                payload: itsOn,
                source: "sonoff"
            }, null]);
        }
        break;
    }

    // ask for IP address
    node.send([null, {
        topic: parts.slice(null, -1).concat("IPAddress1").join("/"),
        payload: ""
    }]);
}


function findDeviceByTopicAndChannel(topic, channel) {
    let devices = G.getBuilding(accountId, buildingId).devices;

    for (var dId in devices) {
        let device = devices[dId];

        if (device.channel && device.topic && device.channel == channel && device.topic == topic) {
            return dId;
        }
    }
}

