function indexPeoplesDevices(people) {
    return Object.entries(people).reduce((a, [personId, {devices: {wifi = []} = {}}]) => 
        ({...a, ...wifi.reduce((x, {mac, definitivePresence}) => ({...x, [mac]: {personId, definitivePresence}}), {})}), {})
}

function indexDeviceMacs(building) {
    const {devices = {}} = building;
    return Object.entries(devices).reduce((a, [deviceId, {macAddress}]) => {
        if (macAddress)
            a[macAddress] = deviceId;

        return a;
    }, {})
}

function indexRules(building) {
    let eventRules = {};
    const {accountId, buildingId, rules = {}} = building;

    node.status({ text: G.shortestDateTime() + ": indexing rules." });

    Object.entries(rules).map(([ruleId, rule]) => {
        const {events: ruleEvents = []} = rule;

        ruleEvents.forEach(eventId => {
            eventRules[eventId] = (eventRules[eventId] || []).concat([ruleId]);
        });

        let facts = new Set();

        if (G.ruleHasConditions(rule)) {
            getFactsForConditions(rule.conditions.all || rule.conditions.any || [], facts);
        }

        getFactsForActions(rule, facts);

        rule.facts = Array.from(facts)
    });

    return eventRules;
}


function getFactsForConditions(block, facts) {
    block.map(({all, any, fact}) => {
        if (all) getFactsForConditions(all, facts);
        else if (any) getFactsForConditions(any, facts);
        else if (fact) {
            const parts = fact.split(".");
            const lastPart = parts[parts.length - 1];

            let variable = "value";
            if (["previousValue", "value", "timestamp", "previousTimestamp"].includes(lastPart)) {
                variable = lastPart;
                parts.splice(-1);
            }

            const factRoot = parts.join(".");

            const fqFact = [factRoot, variable].join(".");
            facts.add(fqFact);
        }
    });
}


function getFactsForActions({actions = {}}, facts) {
    Object.entries(actions).map(([actionType, action]) => 
        getFactsForActionBlock(action, facts)
    );
}


function getFactsForActionBlock(actionBlock, facts) {
    // hoiks tokens out of rule actions to find facts we'll need to gather in order to detokenise
    if (Array.isArray(actionBlock)) {
        actionBlock.map(action =>
            Object.values(action).map(val => {
                if (typeof val === "string") {
                    const getMatches = (str, rx, callback) => {
                        let results;
                        while ((results = rx.exec(str)) !== null) callback(results);
                    }

                    getMatches(val, /%([^%]*)%/g, results => {
                        const key = results[1];
                        const parts = key.split(".");
                        const lastPart = parts[parts.length - 1];

                        let variable = "value";
                        if (["previousValue", "value", "timestamp", "previousTimestamp"].includes(lastPart)) {
                            variable = lastPart;
                            parts.splice(-1);
                        }

                        const fqFact = [...parts, variable].join(".");
                        facts.add(fqFact)
                    })
                }
            })
        );
    }
}


function addVirtualGroups(building) {
    if (!building.groups) building.groups = {};

    for (let deviceId in building.devices) {
        let device = building.devices[deviceId];
        const room = device.room || device.roomId

        if (device.light && room) {  // it's a light with a designated room
            let groupId = "all" + room + "lights";
            const label = room.charAt(0).toUpperCase() + room.slice(1);    // title case rome

            if (!building.groups[groupId]) {
                building.groups[groupId] = {
                    description: ["All", label, "Lights"].join(" "),
                    devices: [],
                    wemoEmulation: true,
                    virtual: true
                };
            }

            if (building.groups[groupId].virtual)
                building.groups[groupId].devices.push(deviceId);
        }
    }
}


