}

const [G, L, accounts] = global.get(["globals", "lodash", "accounts"]);
const NOBUILDING = "undefined";

const getInitialScores = obj => G.buildObjectFromEntries(obj, ([accountId]) => ({ [accountId]: { scores: {} } }));
const recalculateBuildingScores = obj =>
    G.buildObjectFromEntries(obj, ([source, {buildingId = NOBUILDING, score = 0}], a) =>
        ({[[buildingId]]: (a[buildingId] || 0) + score}));

const presence = global.get("presence") || getInitialScores(accounts);

const { accountId, personId, buildingId = NOBUILDING, source = "unknown", score = 1 } = msg;
const {score: sourceScore, buildingId: sourceBuildingId} = L.get(presence, [accountId, personId, "sources", source], {});

if (sourceScore === score && sourceBuildingId === buildingId) {
    return [null, null, null, {topic: [accountId, personId, source].join("."), payload: "unchanged"}];
} else {
    const aPres = G.ensureProp(presence, accountId, {});
    const pPres = G.ensureProp(aPres, personId, {sources: {}});

    pPres.sources[source] = {buildingId, score};
    pPres.scores = Object.assign({[NOBUILDING]: 0}, recalculateBuildingScores(pPres.sources));
    const {k: atBuilding} = G.objectReduce(pPres.scores, (a, [k, v]) => v > a.v ? {v, k} : a, {v: -999999, k: null});
    const prevBuildingId = pPres.buildingId; // save previous building
    pPres.buildingId = atBuilding; // set current building
    pPres.timestamp = Date.now(); // and update the timestamp

    // We can send direct events from here... but someone/noone events are generated from presence.count events by static rules created in loadA&B

    // Send person@building
    node.send([null, {accountId, topic: ["presence", personId, "buildingId"].join("."), payload: atBuilding, source: "presencecore", scores: pPres.scores}, null, null]);
    node.send([{topic: [accountId, buildingId, "presence", personId].join("."), payload: buildingId === atBuilding, source: "presencecore", scores: pPres.scores}, null, null, null]);

    if (prevBuildingId !== atBuilding) {    // building changed
        if (atBuilding === NOBUILDING) // decide whether to send arrival or departure event
            node.send([null, null, {accountId, buildingId: prevBuildingId, topic: ["presence", "departure"].join("."), payload: personId, source: "presencecore"}, null]);
        else
            node.send([null, null, {accountId, buildingId: atBuilding, topic: ["presence", "arrival"].join("."), payload: personId, source: "presencecore"}, null]);
    }

    global.set("presence", presence);

    const getBuildingPresenceList = (aId, bId) =>
        Object.keys(
            G.buildObjectFromEntries(presence[aId], ([k, { buildingId }], a) =>
                buildingId === bId ? Object.assign(a, {[k]: true }) : a
            )
        );
    const buildingCounts = G.buildObjectFromEntries(accounts, ([accountId, { buildings }]) => ({
        [accountId]: G.buildObjectFromEntries(buildings, ([buildingId = NOBUILDING]) => {
            const list = getBuildingPresenceList(accountId, buildingId);
            return { [buildingId]: { list, count: list.length } };
        })
    }));

    // this is called for each person whenever there's an upstream event... sooooo there's...
    // massive duplication caught later by RBE node
    Object.entries(buildingCounts).forEach(([accountId, account]) =>
        Object.entries(account).forEach(([buildingId, {list, count}]) => {
            // send building people count
            node.send([{topic: [accountId, buildingId, "presence", "count"].join("."), payload: count, source: "presencecore"}, null, null, null]);
            // send building people list
            node.send([{topic: [accountId, buildingId, "presence", "people"].join("."), payload: list, source: "presencecore"}, null, null, null]);
        })) 

    global.set("buildingCounts", buildingCounts);

    return [null, null, null, presence];
}

